mapper.select:
SELECT drinkbill.id, proName, drinbillCode, productName, proCount, drinkbill.remark, unit, drinkbill.createDate, drinkbill.createBy, providerID, totalCost, price, isPay FROM mydiningroom.drinkbill,provider
and provider.id=providerId
and providerId=#{providerId}
and productName like concat("%",#{productName},"%")
order By createDate desc LIMIT #{firstData}, #{pageSize}

select count(1) from drinkbill


        and providerId=#{providerId}


        and productName like concat("%",#{productName},"%")

mapper.update:
UPDATE mydiningroom.drinkbill
drinbillCode = #{drinbillCode} , productName = #{productName} , proCount = #{proCount} , remark = #{remark} , unit = #{unit} , isPay = #{isPay},
WHERE id = #{id} ;

mapper.select:
SELECT drink_sell_bill.id, drinkbillID, sellCount, sellPrice, drink_sell_bill.DrinkBillCode, productName FROM mydiningroom.drink_sell_bill ,drinkbill,provider WHERE drinkbill.drinbillCode=drink_sell_bill.DrinkBillCode and drinkbill.providerId=provider.id
and provider.id=#{providerId}
and productName like concat("%",#{productName},"%")
order By drinkbill.createDate desc LIMIT #{firstData}, #{pageSize}

select count(1) from drink_sell_bill,provider,drinkbill where
drinkbill.drinbillCode=drink_sell_bill.DrinkBillCode
and drinkbill.providerId=provider.id

    and provider.id=#{providerId}


    and providerId=#{providerId}


    and productName like concat("%",#{productName},"%")

SELECT a.id as drinkBillid, b.id as drink_sell_billID, a.productName, proCount, unit, b.drinkbillCode , sellCount, a.proCount-b.sellCount AS suplusCount, sellPrice FROM drinkbill a,drink_sell_bill b,provider c WHERE a.providerID=c.id AND b.DrinkBillCode=a.drinbillCode
and c.id=#{providerId}
and productName like concat("%",#{productName},"%")
order By a.createDate desc LIMIT #{firstData}, #{pageSize}

SELECT id, proName, product, telNumber, address, contact, phone, remark, createDate FROM mydiningroom.provider where 1=1
and proName like concat('%',#{serchProName},'%') or product like concat('%',#{serchProName},'%')
and id=#{proId}
order by createDate desc LIMIT #{firstData}, #{pageSize}

SELECT count(1) FROM mydiningroom.provider where 1=1
and proName like concat('%',#{serchProName},'%') or product like concat('%',#{serchProName},'%')
and id=#{proId}

mapper.update:
UPDATE mydiningroom.provider
proName = #{proName} , product =#{product} , telNumber = #{telNumber} , address = #{address} , contact = #{contact} , phone = #{phone} , remark = #{remark} ,
WHERE id = #{id} ;
